mapper.select:

select r_id, c_id, m_id, r_content, r_regdate
from 
(
	select /*+INDEX(tbl_reply idx_reply)+*/
		rownum rn, r_id, c_id, m_id, r_content, r_regdate
	from tbl_reply
	where c_id = #{c_id}
	and r_id > 0
	and rownum <= #{paging.pageNum} * #{paging.amount}
)
where rn > (#{paging.pageNum} -1) * #{paging.amount}
