mapper.select:
select *
from aircamp.tbl_gallery g
where g.g_id=#{g_id}

select * from (select /*+INDEX_DESC(tbl_item pk_item)*/ rownum rn,tbl_item.i_id, tbl_item.m_id, tbl_item.i_sdate, tbl_item.i_edate, tbl_item.i_score, tbl_item.i_eval, tbl_item.i_regdate, tbl_item.i_title, tbl_item.i_price, tbl_item.i_info, tbl_item.i_phone, tbl_item.i_email, tbl_item.i_katalk, tbl_item.i_addr, tbl_item.i_status, g.g_id from tbl_item left outer join (select tbl_item.i_id,min(tbl_gallery.g_id) g_id from tbl_gallery , tbl_item where tbl_gallery.i_id=tbl_item.i_id group by tbl_gallery.i_id) g on g.i_id=tbl_item.i_id where i_status=0
and i_addr like '%'||#{iAddr}||'%'
and i_sdate >= #{iSdate}
and i_edate <= #{iEdate}
and i_title like '%'||#{iTitle}||'%'
)t
where rn <= (#{pageNum} * 10) and rn > ((#{pageNum} -1) * 10)

select count(*) from (select * from tbl_item left outer join (select tbl_item.i_id, MIN(tbl_gallery.g_id) g_id from tbl_gallery , tbl_item where tbl_gallery.i_id=tbl_item.i_id group by tbl_item.i_id, tbl_gallery.i_id) g on g.i_id=tbl_item.i_id) where 1=1 and i_status=0
and i_addr like '%'||#{iAddr}||'%'
and i_sdate >= #{iSdate}
and i_edate <= #{iEdate}
and i_title like '%'||#{iTitle}||'%'
